INSERT INTO `lgr_centralcontent` (`id`, `index`, `subject_id`, `subject`, `year`, `topic_id`, `content_id`, `topic`, `content`) VALUES
(2,	'BI.46.1.1',	'BI',	'BIOLOGI',	'4--6',	1,	1,	'Natur och samhälle',	'Människans beroende av och påverkan på naturen och vad detta innebär för en  hållbar utveckling. Ekosystemtjänster, till exempel nedbrytning, pollinering och  rening av vatten och luft.'),
(3,	'BI.46.1.2',	'BI',	'BIOLOGI',	'4--6',	1,	2,	'Natur och samhälle',	'Djurs, växters och andra organismers liv. Fotosyntes, förbränning och ekologiska samband och vilken betydelse kunskaper om detta har, till exempel för jordbruk och fiske.'),
(4,	'BI.46.1.3',	'BI',	'BIOLOGI',	'4--6',	1,	3,	'Natur och samhälle',	'Ekosystem i närmiljön, samband mellan olika organismer och namn på vanligt förekommande arter. Samband mellan organismer och den icke levande miljön.'),
(5,	'BI.46.1.4',	'BI',	'BIOLOGI',	'4--6',	1,	4,	'Natur och samhälle',	'Naturen som resurs för rekreation och upplevelser och vilket ansvar vi har när vi nyttjar den.'),
(6,	'BI.46.2.1',	'BI',	'BIOLOGI',	'4--6',	2,	1,	'Kropp och hälsa',	'Hur den psykiska och fysiska hälsan påverkas av sömn, kost, motion, sociala  relationer och beroendeframkallande medel. Några vanliga sjukdomar och hur de kan förebyggas och behandlas.'),
(7,	'BI.46.2.2',	'BI',	'BIOLOGI',	'4--6',	2,	2,	'Kropp och hälsa',	'Människans organsystem. Organens namn, utseende, placering, funktion och  samverkan.'),
(8,	'BI.46.2.3',	'BI',	'BIOLOGI',	'4--6',	2,	3,	'Kropp och hälsa',	'Människans pubertet, sexualitet och reproduktion samt frågor om identitet,  jämställdhet, relationer, kärlek och ansvar.'),
(9,	'BI.46.3.1',	'BI',	'BIOLOGI',	'4--6',	3,	1,	'Biologin och världsbilden',	'Några historiska och nutida upptäckter inom biologiområdet och deras betydelse för människans levnadsvillkor och syn på naturen.'),
(10,	'BI.46.3.2',	'BI',	'BIOLOGI',	'4--6',	3,	2,	'Biologin och världsbilden',	'Olika kulturers beskrivningar och förklaringar av naturen i skönlitteratur, myter och konst och äldre tiders naturvetenskap.'),
(11,	'BI.46.3.3',	'BI',	'BIOLOGI',	'4--6',	3,	3,	'Biologin och världsbilden',	'Livets utveckling och organismers anpassningar till olika livsmiljöer.'),
(12,	'BI.46.4.1',	'BI',	'BIOLOGI',	'4--6',	4,	1,	'Biologins metoder och arbetssätt',	'Enkla fältstudier och experiment. Planering, utförande och utvärdering.'),
(13,	'BI.46.4.2',	'BI',	'BIOLOGI',	'4--6',	4,	2,	'Biologins metoder och arbetssätt',	'Hur djur, växter och andra organismer kan identifieras, sorteras och grupperas.'),
(14,	'BI.46.4.3',	'BI',	'BIOLOGI',	'4--6',	4,	3,	'Biologins metoder och arbetssätt',	'Dokumentation av enkla undersökningar med tabeller, bilder och enkla skriftliga rapporter.'),
(15,	'BI.46.4.4',	'BI',	'BIOLOGI',	'4--6',	4,	4,	'Biologins metoder och arbetssätt',	'Tolkning och granskning av information med koppling till biologi, till exempel i faktatexter och tidningsartiklar.'),
(16,	'BI.79.1.1',	'BI',	'BIOLOGI',	'7--9',	1,	1,	'Natur och samhälle',	'Människans påverkan på naturen lokalt och globalt. Möjligheter att som konsument och samhällsmedborgare bidra till en hållbar utveckling.'),
(17,	'BI.79.1.2',	'BI',	'BIOLOGI',	'7--9',	1,	2,	'Natur och samhälle',	'Ekosystems energiflöde och kretslopp av materia. Fotosyntes, förbränning och andra ekosystemtjänster.'),
(18,	'BI.79.1.3',	'BI',	'BIOLOGI',	'7--9',	1,	3,	'Natur och samhälle',	'Biologisk mångfald och vad som gynnar respektive hotar den. Samhällsdiskussioner om biologisk mångfald, till exempel i samband med skogsbruk och jakt.'),
(19,	'BI.79.1.4',	'BI',	'BIOLOGI',	'7--9',	1,	4,	'Natur och samhälle',	'Lokala ekosystem och hur de kan undersökas utifrån ekologiska frågeställningar. Sambanden mellan populationer och tillgängliga resurser i ekosystem. De lokala ekosystemen i jämförelse med regionala eller globala ekosystem.'),
(20,	'BI.79.1.5',	'BI',	'BIOLOGI',	'7--9',	1,	5,	'Natur och samhälle',	'Aktuella samhällsfrågor som rör biologi.'),
(21,	'BI.79.2.1',	'BI',	'BIOLOGI',	'7--9',	2,	1,	'Kropp och hälsa',	'Hur den fysiska och psykiska hälsan påverkas av sömn, kost, motion, sociala relationer och beroendeframkallande medel. Vanligt förekommande sjukdomar och hur de kan förebyggas och behandlas. Virus, bakterier, infektioner och smittspridning. Antibiotika och resistenta bakterier.'),
(22,	'BI.79.2.2',	'BI',	'BIOLOGI',	'7--9',	2,	2,	'Kropp och hälsa',	'Kroppens celler, organ och organsystem och deras uppbyggnad, funktion och  samverkan. Evolutionära jämförelser mellan människan och andra organismer.'),
